def get_content_function_name(self, path_info):
    if path_info=="/":
        return "root"
    elif path_info=="/create_news_item":
        return "create_news_item_page"
    elif path_info=="/news/":
        return "news"
    elif path_info=="/rss/":
        return "rss"
    else:
	# TODO: should loop through bases?
	content_function_name = Authentication.get_content_function_name(self, path_info)
	if content_function_name == None:
		return Neighbours.get_content_function_name(self, path_info)





